import React, { Component, Fragment } from 'react';
import Link from 'next/link';
import DateFormatter from './DateFormatter';

export default class BlogItem extends Component {
  render() {
    const { title, intro, createdAt, userId } = this.props.blog;

    return (
      <Fragment>
        <div className="content is-medium blog">
          <div>
            <h3 className="title has-text-centered">{this.props.blog.title}</h3>
            <p className="subtitle is-6 has-text-centered">
              <Link href="#">
                <a>@jollygrin</a>
              </Link>
              <DateFormatter timestamp={this.props.blog.createdAt} />
            </p>
          </div>
          <p className="has-text-centered is-size-5 blog-intro">
            {this.props.blog.intro}
          </p>
          <div>
            <div className="is-pulled-right is-size-7">
              <Link href="#">
                <a>Read More</a>
              </Link>
            </div>
          </div>
        </div>

        <style jsx>{`
          .blog {
            padding-bottom: 2rem;
            border-bottom: 2px solid whitesmoke;
          }
          .blog-intro {
            margin-top: 2rem;
          }
        `}</style>
      </Fragment>
    );
  }
}
